'use strict';

var _ = require('lodash');
var hdbext = require('@sap/hdbext');
var logger = require('../../../logging').logger;

exports = module.exports;
exports.connect = connect;

function connect(hanaOptions, cb) {
  var pool = hdbext.getPool(_bareConnection(hanaOptions));
  pool.acquire(hanaOptions, function (err, client) {
    if (err) {
      return cb(err);
    }

    client.setAutoCommit(false);
    client.on('error', function onerror(err) {
      logger.error(err, 'HANA connection error');
    });
    cb(null, client);
  });
}

function _bareConnection(hanaService) {
  return _.omit(hanaService, ['schema', 'autoCommit', 'isolationLevel', 'locale', 'session']);
}
