﻿using User from './User';
using Id from './User';

		entity Address {
		    key adid : Id;
		    usid : String(4);
		    city : String(100);
		    strt : String(100);
		    hnum : Integer;
		};

		entity Cars {
		    key crid : Id;
		    usid : String(4);
		    name : String(100);

    		toUser : association to one User on toUser.usid = usid;
		};

using User as _User from '../db/User';
using Address as _Address from '../db/ExtraInfo';
using Cars as _Cars from '../db/ExtraInfo';

service odata {

  entity Users @(
		title: 'Users',
		Capabilities: {
			InsertRestrictions: {Insertable: false},
			UpdateRestrictions: {Updatable: false},
			DeleteRestrictions: {Deletable: false}
		}
	) as projection on _User;

  entity Address @(
		title: 'Address',
		Capabilities: {
			InsertRestrictions: {Insertable: false},
			UpdateRestrictions: {Updatable: false},
			DeleteRestrictions: {Deletable: false}
		}
	) as projection on _Address;

    entity Cars @(
		title: 'Cars',
		Capabilities: {
			InsertRestrictions: {Insertable: false},
			UpdateRestrictions: {Updatable: false},
			DeleteRestrictions: {Deletable: false}
		}
	) as projection on _Cars;

}

﻿type Id : String(4);
using Cars from './ExtraInfo';
using Address from './ExtraInfo';

entity User {
    key usid : Id;
    name : String(100);

    toCars : association to many Cars on toCars.usid = usid;
    toAddress : association to one Address on toAddress.usid = usid;
};
